import * as esbuild from "esbuild-wasm";
import axios from "axios";

export const unpkgPathPlugin = () => {
  return {
    name: "unpkg-path-plugin",
    setup(build: esbuild.PluginBuild) {
      build.onResolve({ filter: /.*/ }, async (args: any) => {
        console.log("onResolve", args);

        const { importer, path, resolveDir } = args;

        if (importer !== "index.js") {
          if (path.includes("./") || path.includes("../")) {
            const { href } = new URL(path, `https://unpkg.com${resolveDir}/`);

            return { path: href, namespace: "a" };
          }

          return { path: `https://unpkg.com/${path}`, namespace: "a" };
        }

        return { path, namespace: "a" };
      });

      build.onLoad({ filter: /.*/ }, async (args: any) => {
        console.log("onLoad", args);

        if (args.path === "index.js") {
          return {
            loader: "jsx",
            contents: `
              import React from 'react'
              console.log(react)
            `,
          };
        }

        const { data, request } = await axios.get(args.path);

        return {
          loader: "jsx",
          contents: data,
          resolveDir: new URL("./", request.responseURL).pathname,
        };
      });
    },
  };
};
